import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTranslation } from 'react-i18next';

const Properties = () => {
  const [properties, setProperties] = useState([]);
  const [filters, setFilters] = useState({ type: '', minPrice: '', maxPrice: '' });
  const { t } = useTranslation();

  const fetchProperties = async () => {
    try {
      const res = await axios.get('/api/properties', { params: filters });
      setProperties(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProperties();
  }, [filters]);

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6 text-primary">{t('properties')}</h2>

      {/* Filters */}
      <div className="mb-6 p-4 card bg-gray-50">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <select
            name="type"
            value={filters.type}
            onChange={handleFilterChange}
            className="input"
          >
            <option value="">All Types</option>
            <option value="rent">Rent</option>
            <option value="buy">Buy</option>
          </select>
          <input
            name="minPrice"
            type="number"
            placeholder="Min Price"
            value={filters.minPrice}
            onChange={handleFilterChange}
            className="input"
          />
          <input
            name="maxPrice"
            type="number"
            placeholder="Max Price"
            value={filters.maxPrice}
            onChange={handleFilterChange}
            className="input"
          />
          <button onClick={fetchProperties} className="btn-primary">
            Filter
          </button>
        </div>
      </div>

      {/* Listings */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {properties.map((prop) => (
          <div key={prop.id} className="card">
            {prop.image_url && (
              <img
                src={prop.image_url}
                alt={prop.title}
                className="w-full h-48 object-cover rounded-t-lg"
              />
            )}
            <h3 className="font-bold text-lg mt-2">{prop.title}</h3>
            <p className="text-secondary font-bold">â‚¬{prop.price}</p>
            <p className="text-sm text-gray-600">Type: {prop.type}</p>
            <p className="text-sm">Owner: {prop.owner_name}</p>
            <p className="mt-2 text-sm">{prop.description}</p>
            <button className="btn-secondary mt-4 w-full">
              Contact Owner
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Properties;
